import React from 'react';
import logo from './logo.svg';
import {BrowserRouter, Route, Link, Switch, Redirect} from 'react-router-dom';
import {connect} from 'react-redux';
import * as chatActions from './store/actions/chatActions';
import * as AuthActions from './store/actions/authActions';
import Messenger from './components/pages/Messenger';
import Auth from './components/pages/Auth';
import "./assets/css/swag.css";


class App extends React.Component{
  componentDidMount() {
    this.props.setupSocket(this.props.token);
  }
  render(){
    return (
      <div className="App">

        <button onClick = {e => {
          this.props.logout();
        }}>Logout</button>

        <BrowserRouter>

          <Switch>

            <Route 
              path = "/login"
              render ={props => {
                if(this.props.token){
                  return(
                    <Redirect to ="/" />
                  )
                }
                else{
                  return(
                    <Auth />
                  )
                }
              }}
            />

            <Route 
              path = "/signup"
              render ={props => {
                if(this.props.token){
                  return(
                    <Redirect to ="/" />
                  )
                }
                else{
                  return(
                    <Auth />
                  )
                }
              }}
            />

            <Route 
              path = "/:threadId"
              render = {props => {
                if(!this.props.token){
                return (
                  <Redirect to="/login" />
                )
                }
                else{
                  return (
                    <Messenger />
                  ) 
                }

              }}
            />

            <Route 
              path = "/"
              render = {props => {
                if(!this.props.token){
                return (
                  <Redirect to="/login" />
                )
                }
                else{
                  return (
                    <Messenger />
                  ) 
                }

              }}
            />
          </Switch>
        </BrowserRouter>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  ...state.auth,
  ...state.chat
})

const mapDispatchToProps = dispatch => ({
  setupSocket: (token) =>{
    dispatch(chatActions.setupSocket(token));
  }, 
  logout: () => {
    dispatch(AuthActions.logout());
  }
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(App);
