const defaultState = {
    socket: null,
    message: '',
    threads: [],
    currentThread: '',
    users: []
}

const chat = (state = defaultState, action) =>{
    switch(action.type){
        case 'SETUP_SOCKET':
            return {
                ...state,
                socket: action.payload
            }
        case 'GOT_USERS':
            console.log('Getting User', action.payload);
            return {
                ...state,
                users: action.payload
            }
        case 'ADD_THREAD':
            return {
                ...state,
                threads: state.threads.filter(t => t.id === action.payload.id).length === 0 ?
                    state.threads.concat(action.payload)
                : state.threads
            }
        
        case 'INITIAL_THREADS':
            return {
                ...state,
                threads: action.payload
            }
        case 'ADD_MESSAGES_TO_THREAD':
            return {
                ...state,
                threads: state.threads.map(t => {
                    if(t.id === action.payload.threadId){
                        return {
                            ...t,
                            message: action.payload.message.concat(t.Message)
                        }
                    }
                    else{
                        return t
                    }
                })
            }
        default:
            return state
    }
}

export default chat;
